<span class="line">    <span class="token keyword">this</span><span class="token punctuation">.</span>names <span class="token operator">=</span> <span class="token punctuation">[</span><span class="token string">&quot;kevin&quot;</span><span class="token punctuation">,</span> <span class="token string">&quot;daisy&quot;</span><span class="token punctuation">]</span><span class="token punctuation">;</span></span>
